}

/* Modal -- form */
.backdrop {
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.2);
  position: fixed;
  top: 0;
  left: 0;
  transition: opacity 400ms, visibility 400ms;
  z-index: 50;
}

.backdrop.is-hidden .modal {
  transform: scale(0.5);
}

.modal {
  width: 528px;
  min-height: 581px;
  background-color: #fff5f6;
  border-radius: 5px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: scale(1) translate(-50%, -50%);
  transition: transform 400ms;
  padding: 40px;
}
@media screen and (max-width: 1200px) {
  .modal {
    width: 450px;
    height: 609px;
  }
}

.modal__button-commit {
  width: 200px;
  height: 50px;
  border-radius: 22px;
  background-color: #d41443;
  color: #ffffff;
  position: relative;
  left: 50%;
  transform: translate(-50%, 0);
  border: none;
}

.modal__close {
  position: absolute;
  top: 14px;
  right: 14px;
  width: 30px;
  height: 30px;
  background-color: transparent;
  border: 1px solid #fff5f6;
  display: flex;
  align-items: center;
  justify-content: center;
}

.modal__close-icon {
  transition: fill 250ms linear;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  color: #ffffff;
  border: none;
}
.modal__close-icon:hover {
  fill: #d41342;
}

.modal__close {
  border: none;
}

.is-hidden {
  visibility: hidden;
  opacity: 0;
  pointer-events: none;
}

.modal__title {
  font-style: normal;
  font-weight: 700;
  font-size: 20px;
  line-height: 1.15;
  text-align: center;
  letter-spacing: 0.03em;
  margin-bottom: 12px;
  margin-top: 3px;
  color: #d41443;
}

.modal__form-input {
  width: 100%;
  height: 40px;
  border: 1px solid rgba(33, 33, 33, 0.2);
  border-radius: 4px;
  outline: none;
  padding-left: 42px;
  padding-right: 15px;
  background-color: transparent;
  transition: border-color 250ms linear;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}
.modal__form-input:focus {
  border-color: #d41443;
}
.modal__form-input:focus + .modal-input-icon {
  fill: #d41443;
}

.modal__form-input-text {
  font-style: normal;
  font-weight: normal;
  font-size: 12px;
  line-height: 1.17;
  letter-spacing: 0.01em;
  color: #d41443;
  display: block;
  margin-bottom: 4px;
}

.modal__form-field {
  margin-bottom: 10px;
}

.modal__form-field-last {
  margin-bottom: 0;
}

.modal__form-input-comment {
  width: 100%;
  min-height: 120px;
  border: 1px solid rgba(33, 33, 33, 0.2);
  border-radius: 4px;
  outline: none;
  padding: 12px 16px;
  background-color: transparent;
  resize: none;
}
.modal__form-input-comment::placeholder {
  font-style: normal;
  font-weight: 400;
  font-size: 12px;
  line-height: 1.17;
  letter-spacing: 0.01em;
  color: rgba(117, 117, 117, 0.5);
}
.modal__form-input-comment:focus {
  border-color: #d41443;
}

.modal-input-icon {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  left: 12px;
  transition: fill 250ms linear;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.modal__form-input-wrap {
  position: relative;
}

.modal__form-checkbox {
  margin-top: 20px;
  margin-bottom: 30px;
}

.modal__checkbox-text {
  font-style: normal;
  font-weight: 400;
  font-size: 14px;
  line-height: 1.71;
  letter-spacing: 0.03em;
  color: #d41443;
  position: relative;
  display: flex;
  align-items: center;
}
.modal__checkbox-text::before {
  content: "";
  width: 38px;
  height: 22px;
  border-radius: 50px;
  border: 1px solid #ffffff;
  margin-right: 4px;
  background-color: #ffffff;
  transition: background-color 250ms linear;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}
.modal__checkbox-text::after {
  content: "";
  position: absolute;
  left: 1px;
  top: 2px;
  width: 20px;
  height: 20px;
  background-color: #d41443;
  border-radius: 50%;
  transition: transform 250ms linear, background-color 250ms linear;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}
@media screen and (max-width: 1200px) {
  .modal__checkbox-text {
    font-style: normal;
    font-weight: normal;
    font-size: 12px;
    line-height: 1.16;
    letter-spacing: 0.03em;
    color: #d41443;
  }
  .modal__checkbox-text::before {
    content: "";
    width: 25px;
    height: 8px;
    border-radius: 50px;
    border: 1px solid #d41443;
    margin-right: 4px;
    background-color: #ffffff;
    transition: background-color 250ms linear;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  }
  .modal__checkbox-text::after {
    content: "";
    position: absolute;
    left: 1px;
    top: 3px;
    width: 8px;
    height: 8px;
    background-color: #d41443;
    border-radius: 50%;
    transition: transform 250ms linear, background-color 250ms linear;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  }
}

.modal__checkbox:checked + .modal__checkbox-text::after {
  transform: translateX(17px);
  background-color: #d41443;
}

.modal__checkbox:checked + .modal__checkbox-text::before {
  background-color: #d41443;
}

.modal__checkbox {
  color: #d41443;
  background-color: #d41443;
}

.modal__link {
  color: #d41443;
  margin-left: 5px;
}
@media screen and (min-width: 480px) {
  .modal__link {
    font-style: normal;
    font-weight: normal;
    font-size: 12px;
    line-height: 1.16;
    letter-spacing: 0.03em;
  }
}

