  }
.hero__passion {
  font-family: Titan One;
  font-weight: 400;
  font-size: 48px;
  line-height: 96.5%;
  text-transform: uppercase;
  color: #d41443;
}

.hero__list {
  display: flex;
  list-style: none;
  padding: 0;
  margin-top: 19px;
  margin-bottom: 0px;
}

.hero__item {
  width: auto;
  height: 24px;
}

.hero__item + .hero__item {
  margin-left: 15px;
}

.hero__link {
  display: flex;
  align-items: center;
  text-align: center;
  height: 100%;
  width: 100%;
  font-family: DM Sans;
  font-style: normal;
  font-weight: normal;
  font-size: 8px;
  line-height: 10px;
  border-radius: 20px;
  padding-left: 14px;
  padding-right: 14px;
  text-decoration: none;
}

.hero__link--product {
  color: #ffffff;
  background-color: #d41443;
}

.hero__link--made {
  color: #d41443;
  background-color: #ffffff;
}
