sources.sass\\_products.scss:
.products {
  background-image: #e5e5e5;
  padding-top: 121px;
  padding-bottom: 98px;
}

.products__box {
  text-align: center;
  margin-bottom: 144px;
}
.pre-title {
  font-family: Titan One;
  font-weight: normal;
  font-size: 18px;
  line-height: 1.16;
  //   text-align: center;
  text-transform: uppercase;
  color: $main-current-color;
}
.products-title {
  font-family: Titan One;
  font-style: normal;
  font-weight: normal;
  font-size: 34px;
  line-height: 1.14;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: $main-title-color;
  margin-top: 15px;
}
.ice-cream {
  background-color: $main-current-color;
  width: 280px;
  height: 485px;
  border-radius: 24px;
  margin-left: auto;
  margin-right: auto;
}
.products-list__title--ice-cream {
  position: relative;
  font-family: Titan One;
  font-weight: normal;
  font-size: 20px;
  line-height: 1.15;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: $main-text-color;
  margin-bottom: 60px;
  padding-top: 161px;

  &::before {
    display: block;
    content: '';
    background-image: url(../images/home-pic1.png);
    background-repeat: no-repeat;
    width: 249px;
    height: 252px;
    position: absolute;
    margin-left: 16px;
    margin-right: 15px;
    bottom: 45px;
    z-index: 5;
  }
  &::after {
    content: '';
    display: block;
    width: 30px;
    height: 6px;
    background-image: url(../images/Ellipse-dot-3x.png);
    background-repeat: no-repeat;
    position: absolute;
    left: 125px;
    bottom: -30px;
  }
}

.ice-coffee {
  background-color: $second-current-color;
  width: 280px;
  height: 485px;
  border-radius: 24px;
  margin-left: auto;
  margin-right: auto;
}
.products-list__title--ice-coffee {
  position: relative;
  font-family: Titan One;
  font-weight: normal;
  font-size: 20px;
  line-height: 1.15;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: $main-text-color;
  margin-bottom: 60px;
  padding-top: 161px;

  &::before {
    display: block;
    content: '';
    background-image: url(../images/home-pic2.png);
    background-repeat: no-repeat;
    width: 249px;
    height: 252px;
    position: absolute;
    margin-left: 16px;
    margin-right: 15px;
    bottom: 45px;
  }
  &::after {
    content: '';
    display: block;
    width: 30px;
    height: 6px;
    background-image: url(../images/Ellipse-dot-3x.png);
    background-repeat: no-repeat;
    position: absolute;
    left: 125px;
    bottom: -30px;
  }
}
.milkshakes {
  background-color: $third-current-color;
  width: 280px;
  height: 485px;
  border-radius: 24px;
  margin-left: auto;
  margin-right: auto;
}

.products-list__title--milkshakes {
  position: relative;
  font-family: Titan One;
  font-weight: normal;
  font-size: 20px;
  line-height: 1.15;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: $main-text-color;
  margin-bottom: 60px;
  padding-top: 161px;

  &::before {
    display: block;
    content: '';
    background-image: url(../images/home-pic3.png);
    background-repeat: no-repeat;
    width: 249px;
    height: 252px;
    position: absolute;
    margin-left: 16px;
    margin-right: 15px;
    bottom: 45px;
  }
  &::after {
    content: '';
    display: block;
    width: 30px;
    height: 6px;
    background-image: url(../images/Ellipse-dot-3x.png);
    background-repeat: no-repeat;
    position: absolute;
    left: 125px;
    bottom: -30px;
  }
}
.products-list__title {
  font-family: Titan One;
  font-weight: normal;
  font-size: 20px;
  line-height: 1.15;
  margin-top: 161px;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: $main-text-color;
  margin-bottom: 60px;
  padding-top: 161px;
}

.products-list__text {
  max-width: 200px;
  font-family: DM Sans;
  font-style: normal;
  font-weight: bold;
  font-size: 14px;
  line-height: 1.5;
  letter-spacing: 0.04em;
  color: $main-text-color;
  margin-left: auto;
  margin-right: auto;
  text-align: center;
}
.products__button {
  background-color: $second-btn-color;
  border: none;
  padding: 0;
  text-decoration: none;
  display: inline-block;
  border-radius: 50%;
  margin-left: 120px;
  margin-right: 120px;
  margin-top: 30px;
  margin-bottom: 41px;
  z-index: 10;
}
.products__button--image {
  cursor: pointer;
}

// overlay------------------------------------------------------------

.on-hover:hover + .hidden-block {
  opacity: 1;
  transition: 1s;
}
.overlay__caption {
  max-width: 200px;
  font-family: DM Sans;
  font-style: normal;
  font-weight: bold;
  font-size: 14px;
  line-height: 1.5;
  letter-spacing: 0.04em;
  color: $main-text-color;
  margin-left: auto;
  margin-right: auto;
  text-align: center;
  margin-bottom: 111px;
}

.card__body--ice-cream {
  position: relative;
  overflow: hidden;
}
.overlay--ice-cream {
  margin-top: 5px;
  border-radius: 24px;
  position: absolute;
  width: 280px;
  overflow-y: auto;
  background-color: #f88ca5;
  transform: translateY(0);
  opacity: 0;
  transition: 1s;
  // @include transition(transform, null, null, var(--duration), var(--function));
  .card__link--ice-cream:hover &,
  .card__link--ice-cream:focus & {
    transform: translateY(-101%);
    transition: 1s; /*Скорость перехода состояния элемента*/
    opacity: 1;
  }
}
.card__link--ice-cream {
  text-decoration: none;
  transition: box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1);
}

.card__body--ice-coffee {
  position: relative;
  overflow: hidden;
}
.overlay--ice-coffee {
  margin-top: 5px;
  border-radius: 24px;
  position: absolute;
  width: 280px;
  overflow-y: auto;
  background-color: #e8c086;
  transform: translateY(0);
  opacity: 0;
  transition: 1s;
  // @include transition(transform, null, null, var(--duration), var(--function));
  .card__link--ice-coffee:hover &,
  .card__link--ice-coffee:focus & {
    transform: translateY(-101%);
    transition: 1s; /*Скорость перехода состояния элемента*/
    opacity: 1;
  }
}
.card__link--ice-coffee {
  text-decoration: none;
  transition: box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1);
}

.card__body--milkshakes {
  position: relative;
  overflow: hidden;
}
.overlay--milkshakes {
  margin-top: 5px;
  border-radius: 24px;
  position: absolute;
  width: 280px;
  overflow-y: auto;
  background-color: #acd17b;
  transform: translateY(0);
  opacity: 0;
  transition: 1s;
  // @include transition(transform, null, null, var(--duration), var(--function));
  .card__link--milkshakes:hover &,
  .card__link--milkshakes:focus & {
    transform: translateY(-101%);
    transition: 1s; /*Скорость перехода состояния элемента*/
    opacity: 1;
  }
}
.card__link--milkshakes {
  text-decoration: none;
  transition: box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1);
}

sources.sass\\_about.scss:
.about {
  padding-top: 125px;
  @media screen and (max-width: 767px) {
    background-image: url(../images/bg-milk-mob-1x-min.png);
    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url(../images/bg-milk-mob-2x-min.png);
    }
    background-repeat: no-repeat;
    background-size: contain;
    background-position: bottom;
  }
  @media screen and (min-width: 1200px) {
    background-image: url(../images/bg-milk-desk-1x-min.png);
    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url(../images/bg-milk-desk-2x-min.png);
    }
    background-repeat: no-repeat;
    background-size: contain;
    background-position: bottom;
  }
  background: $second-bg-color;
}
.about__pretitle {
  font-family: Titan One;
  font-style: normal;
  font-weight: normal;
  font-size: 18px;
  line-height: 1.17;
  text-align: center;
  text-transform: uppercase;
  color: $fourth-text-color;
  margin-bottom: 15px;
}
.about__title {
  font-family: Titan One;
  font-size: 34px;
  line-height: 39px;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: $main-title-color;
  margin-bottom: 26px;
}
.about__block {
  @media screen and (min-width: 768px) {
    display: flex;
  }
}
.about__img {
