sources.sass\\_gallery.scss:
.gallery {
  @media screen and (min-width: 320px) {
    display: grid;
    grid-template-columns: 40px 40px 80px 40px 40px 40px;
    grid-template-rows: repeat(11, 12px);
    justify-content: center;
    grid-column-gap: 3px;
    grid-row-gap: 2px;

    & > div:nth-child(1) {
      grid-column: 1 / 2;
      grid-row: 4 / 8;
      align-self: end;
    }

    & > div:nth-child(2) {
      grid-column: 2 / 3;
      grid-row: 3 / 7;
      align-self: end;
    }

    & > div:nth-child(3) {
      grid-column: 2 / 3;
      grid-row: 7 / 11;
    }

    & > div:nth-child(4) {
      grid-column: 3 / 4;
      grid-row: 2 / 9;
    }

    & > div:nth-child(5) {
      grid-column: 3 / 4;
      grid-row: 8 / 12;
    }

    & > div:nth-child(6) {
      grid-column: 4 / 5;
      grid-row: 3 / 7;
      align-self: end;
    }

    & > div:nth-child(7) {
      grid-column: 4 / 5;
      grid-row: 7 / 11;
    }

    & > div:nth-child(8) {
      grid-column: 5 / 6;
      grid-row: 2 / 6;
      align-self: end;
    }

    & > div:nth-child(9) {
      grid-column: 5 / 6;
      grid-row: 6 / 10;
    }

    & > div:nth-child(10) {
      grid-column: 6 / 7;
      grid-row: 5 / 9;
    }
  }

  @media screen and (min-width: 768px) {
    display: grid;
    grid-template-columns: 85px 85px 170px 85px 85px 85px;
    grid-template-rows: repeat(11, 25px);
    justify-content: center;
    grid-column-gap: 4px;

    & > div:nth-child(1) {
      grid-column: 1 / 2;
      grid-row: 4 / 8;
      align-self: end;
    }

    & > div:nth-child(2) {
      grid-column: 2 / 3;
      grid-row: 3 / 7;
      align-self: end;
    }

    & > div:nth-child(3) {
      grid-column: 2 / 3;
      grid-row: 7 / 11;
    }

    & > div:nth-child(4) {
      grid-column: 3 / 4;
      grid-row: 2 / 9;
      align-self: end;
    }

    & > div:nth-child(5) {
      grid-column: 3 / 4;
      grid-row: 9 / 13;
    }

    & > div:nth-child(6) {
      grid-column: 4 / 5;
      grid-row: 3 / 7;
      align-self: end;
    }

    & > div:nth-child(7) {
      grid-column: 4 / 5;
      grid-row: 7 / 11;
    }

    & > div:nth-child(8) {
      grid-column: 5 / 6;
      grid-row: 2 / 6;
      align-self: end;
    }

    & > div:nth-child(9) {
      grid-column: 5 / 6;
      grid-row: 6 / 10;
    }

    & > div:nth-child(10) {
      grid-column: 6 / 7;
      grid-row: 5 / 9;
    }
  }
  @media screen and (min-width: 1200px) {
    display: grid;
    grid-template-columns: 140px 140px 280px 140px 140px 140px;
    grid-template-rows: repeat(11, 35px);
    justify-content: center;
    grid-gap: 6px;

    & > div:nth-child(1) {
      grid-column: 1 / 2;
      grid-row: 4 / 8;
      align-self: end;
    }

    & > div:nth-child(2) {
      grid-column: 2 / 3;
      grid-row: 3 / 7;
      align-self: end;
    }

    & > div:nth-child(3) {
      grid-column: 2 / 3;
      grid-row: 7 / 11;
    }

    & > div:nth-child(4) {
      grid-column: 3 / 4;
      grid-row: 2 / 9;
    }

    & > div:nth-child(5) {
      grid-column: 3 / 4;
      grid-row: 9 / 13;
    }

    & > div:nth-child(6) {
      grid-column: 4 / 5;
      grid-row: 3 / 7;
      align-self: end;
    }

    & > div:nth-child(7) {
      grid-column: 4 / 5;
      grid-row: 7 / 11;
    }

    & > div:nth-child(8) {
      grid-column: 5 / 6;
      grid-row: 2 / 6;
      align-self: end;
    }

    & > div:nth-child(9) {
      grid-column: 5 / 6;
      grid-row: 6 / 10;
    }

    & > div:nth-child(10) {
      grid-column: 6 / 7;
      grid-row: 5 / 9;
    }
  }
}

